import React, { useEffect, useState } from "react";
import {connect} from 'react-redux';
import { Link } from 'react-router-dom';
import { axiosWithAuth } from "./Utilities/Utilities";
import Axios from "axios";
import styled from "styled-components"

const Flex= styled.div`
display: flex;
flex-flow: column wrap;
justify-content: center;
margin: 2% 22%;
padding:20px;
background:linear-gradient(to bottom, #747185 , #B7AAB3);
border-radius:10px;
`
const Trip = styled.div`
margin-bottom: 1%;
justify-content:center;
background:linear-gradient(to bottom, #747185 , #B7AAB3);
border-radius:10px;
`

// should display private profile to edit
const PrivateProfile = (props) => {

  const id = props.user.userid;

  console.log(id);

  const [user, setUser] = useState([{}]);
  const [profilee,setProfile] = useState([]);

  useEffect(()=>{
    axiosWithAuth()
    .get(`https://guidr1.herokuapp.com/api/users/data/${id}`)
    .then(res =>{
      console.log(res.data[0]);
      setUser(res.data);
      setProfile(res.data[0])
      
    })
    .catch(error => console.log(error));

  },[])

  
  console.log(user);
  console.log(profilee);
  
  return (
    <Flex>
      {/* {profilee.map(profil=>{
         return(  */}
          
          <div>
       <h1>{profilee}</h1>
            
            <h2>Tagline</h2>
            <p>{props.tagline}</p>
            <p>Guide specialty: {props.guide_specialty}</p>
            <p>age: {props.age}</p>
            <p>Years experience: {props.years_experience}</p>
            </div> 
         {/* )
})}  */}
        {user.map(users=>{

          return(
          <div>
           
           
            <Trip>
              <h2>Trips!</h2>
          <p> {users.trips_title}</p>
          <p>Trip Description: {users.description}</p>
            <p>Trip Duration: {users.duration}</p>
            <p>Trip Distance: {users.distance}</p>
            <p>Trip Date: {users.date}</p>
            </Trip>
          </div>
          )
          
        })}     
      <Link to = '/edits'><button>Edit Profile</button></Link>
    </Flex>
  );
};

const mapStateToProps = state =>{
  return {
      user: state.token,
      profile: state.profile
  }
}

export default connect(
  mapStateToProps,
  {}
) (PrivateProfile);
